    // alert('Test');
    this.setState({
      modalIsOpen : true,
      email : pembeli.email,
      password : pembeli.password,
      saldo : pembeli.saldo,
      // id_pembeli : pembeli.id_pembeli
    });
  }

  closeModal(pembeli){
    this.setState({
      modalIsOpen : false
    });
  }

  onChangeEmail(e){
    this.setState({
      email : e.target.value
    });
  }

  // onChangeId(e){
  //   this.setState({
  //     id_pembeli : e.target.value
  //   });
