import React, { Component } from 'react'

export class Studio extends Component {constructor(props){
  super(props)
  this.state = {
   studios : [],
   modalIsOpen : false,
   id_studio : 0,
   no_studio : ''
  }
  this.openModal = this.openModal.bind(this);
  this.closeModal = this.closeModal.bind(this);
  // this.onChangeId = this.onChangeId.bind(this);
  this.onChangeNo = this.onChangeNo.bind(this);
  this.onSubmit = this.onSubmit.bind(this);
  this.onDelete = this.onDelete.bind(this);
}

openModal(studio){
  // alert('Test');
  this.setState({
    modalIsOpen : true,
    id_studio : studio.id_studio,
    no_studio : studio.no_studio
  });
}

closeModal(studio){
  this.setState({
    modalIsOpen : false
  });
}

onChangeNo(e){
  this.setState({
    no_studio : e.target.value
  });
}

onDelete(studio){
  const obj = {
     id_studio : studio.id_studio
  }
  console.log(obj);

  fetch('http://localhost:5000/api/v1/studio', {
      method: 'DELETE',
      headers: {'Content-Type': 'application/json'},
      body: JSON.stringify(obj)
  }).then((res) => {
    if (res.status >= 400) {
      throw new Error("Bad response from server");
    }
    // return response.json();
  }).then((obj) => {
    if(obj === "success"){
       this.setState({msg: "User has been deleted."});  
    }
    window.location = window.location;
  }).catch(function(err) {
    console.log(err)
});
}

onSubmit(e){
  e.preventDefault();
  const obj = {
    id_studio : this.state.id_studio,
    no_studio : this.state.no_studio
  }
  console.log(obj);
  fetch('http://localhost:5000/api/v1/studio', {
    method : 'PUT',
    headers : {'Content-Type': 'application/json'},
    body : JSON.stringify(obj)
  }).then(res => {
    if(res.status < 400){
      console.log("Success");
    }
    window.location = window.location;
  }).catch((err) => console.log(err));
}

componentDidMount(){
  let self = this;
  fetch('http://localhost:5000/api/v1/studio', {
    method : 'GET'
  }).then((res) => {
    if(res.status >= 400){
      throw new Error("Bad Response");
    }
    return res.json();
  }).then((data) => {
    self.setState({studios: data});
  }).catch(err => {
    console.log('This is Error Code ', err);
  })
}




  render() {
    return (
        <div>
        <nav className="navbar navbar-vertical fixed-left navbar-expand-md navbar-light bg-white" id="sidenav-main">
          <div className="container-fluid">
            {/* <!-- Toggler --> */}
            <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#sidenav-collapse-main"
              aria-controls="sidenav-main" aria-expanded="false" aria-label="Toggle navigation">
              <span className="navbar-toggler-icon"></span>
            </button>
            {/* <!-- Brand --> */}
            <a className="navbar-brand pt-0" href="/">
              <h1>CINEMATIX</h1>
              <hr />
            </a>
            {/* <!-- Collapse header --> */}
            <div className="collapse navbar-collapse" id="sidenav-collapse-main">              
              {/* <!-- Form --> */}
              <form className="mt-4 mb-3 d-md-none">
                <div className="input-group input-group-rounded input-group-merge">
                  <input type="search" className="form-control form-control-rounded form-control-prepended" placeholder="Search"
                    aria-label="Search" />
                  <div className="input-group-prepend">
                    <div className="input-group-text">
                      <span className="fa fa-search"></span>
                    </div>
                  </div>
                </div>
              </form>
              {/* <!-- Navigation --> */}
              <ul className="navbar-nav">
                <li className="nav-item">
                  <a className="nav-link" href="/">
                    <i className="ni ni-tv-2 text-primary"></i> Dashboard
                  </a>
                </li>
